import React, { useState } from "react";
import { FaSearch } from 'react-icons/fa';
import "./searchBar.css";
import axios from 'axios';


const SearchBar = () => {
    const [input, setInput] = useState("");
    const [recipes, setRecipes] = useState([]);

    const app_id = 'd3e066f2';
    const app_key = '3458fa8a64d62c2af3e1ce595bac3d35';


    const handleChange = (e) => {
        setInput(e.target.value);
        fetchData
    };

    const fetchData = (query) => {
        fetch(`https://api.edamam.com/api/recipes/v2?type=public&q=${query}&app_id=d3e066f2&app_key=3458fa8a64d62c2af3e1ce595bac3d35`)
          .then((response) => response.json())
          .then((json) => {
            const json_results = json.filter()
            setRecipes(json.hits);
          })
          .catch((error) => console.error('Error fetching data:', error));
    };

    return (
        <div className="input-container">
            <FaSearch id="searchIcon" />
            <input
                type="text"
                placeholder="Enter Dish Name..."
                onChange={handleChange}
                value={input}
            />
        </div>
    );
};

export default SearchBar;
