import React, { useState } from "react";
import { FaSearch } from 'react-icons/fa';
import "./searchBar.css";


const SearchBar = ({setResults}) => {
    const [input, setInput] = useState("");

    const fetchData = (query) => {
        fetch(`https://api.edamam.com/api/recipes/v2?type=public&q=${query}&app_id=d3e066f2&app_key=3458fa8a64d62c2af3e1ce595bac3d35`)
        .then((response) => response.json())
        .then((json) => {
            // console.log(json.hits);
            const fetch_results = json.filter((hit) => {
                return hit && hit.image && hit.label.toLowerCase().includes(query)
            });
            console.log(fetch_results);
        })
    };

    const handleChange = (value) => {
        setInput(value);
        fetchData(value);
    };

    return (
        <div className="input-container">
            <FaSearch id="searchIcon" />
            <input
                placeholder="Enter Dish Name..."
                onChange={(e) => handleChange(e.target.value) }
                value={input}
            />
        </div>
    );
};

export default SearchBar;
