import React from 'react';
import "./SingleResult.css";

const SingleResult = ({ result }) => {

  const selectedRecipe = (dish) => {
    console.log("selected this dish: " + dish);
    alert("selected this dish: " + dish);
  }

  const handleKeyDown = (event) => {
    console.log("Key pressed:", event.key);
    if (event.key === 'Enter') {
      selectedRecipe(result.recipe.label);
    }
  }

  return (
    <div
      className="singleSearch"
      tabIndex="0"  // Make the div focusable
      onKeyDown={handleKeyDown}  // Add keydown event handler
      onClick={() => selectedRecipe(result.recipe.label)}  // Add click event handler
    >
      {result.recipe.label}
    </div>
  );
}

export default SingleResult;
